actionConfiguration.body:
() => {
  const dataSource = {
    chart: {
      subCaption: "(evaluated using number of orders refunded)",
      lowerlimit: "0",
      upperlimit: "100",
      showvalue: "1",
      numbersuffix: "%",
      theme: "fusion",
      showtooltip: "0"
    },
    colorrange: {
      color: [{
        minvalue: "0",
        maxvalue: "25",
        code: "#F2726F"
      }, {
        minvalue: "25",
        maxvalue: "50",
        code: "#f87171"
      }, {
        minvalue: "50",
        maxvalue: "75",
        code: "#3b82f6"
      }, {
        minvalue: "75",
        maxvalue: "100",
        code: "#62B58F"
      }]
    },
    dials: {
      dial: [{
        value: 100 - Text2Copy1.text.replace('%', '')
      }]
    },
    trendpoints: {
      point: [{
        startvalue: "90",
        displayvalue: "Target",
        thickness: "2",
        color: "#000000",
        usemarker: "1",
        markerbordercolor: "#000000",
        markertooltext: "80%"
      }]
    }
  };
  return {
    type: "angulargauge",
    dataFormat: "json",
    dataSource
  };
}

